// Data Processor - Handle both webhook and manual execution modes
let phoneNumber, messageText, sessionId, startTime;

if ($json.entry && $json.entry[0]?.changes?.[0]?.value?.messages) {
  // Webhook mode - real WhatsApp message
  const whatsappData = $json.entry[0].changes[0].value;
  phoneNumber = whatsappData.messages[0].from;
  messageText = whatsappData.messages[0].text.body;
} else {
  // Manual mode - test data
  phoneNumber = "521234567890";
  messageText = "Hola, necesito ayuda con servicios dentales";
}

sessionId = phoneNumber + "_" + new Date().toISOString().split("T")[0];
startTime = Date.now();

return {
  json: {
    execution_mode: $json.entry ? "webhook" : "manual",
    phone_number: phoneNumber,
    message_text: messageText,
    session_id: sessionId,
    start_time: startTime,
    // AI Agent expects chatInput field (proven working solution)
    chatInput: messageText,
    // Ensure proper structure for other nodes
    messages: [{ text: { body: messageText } }],
    contacts: [{ wa_id: phoneNumber }]
  }
};

// Response Logger - Clean logging logic with proper error handling
const aiResponse = $json.output;
const processingTime = Date.now() - $('Data Processor').item.json.start_time;

// Extract RFC and service information
const rfcMatch = aiResponse.match(/RFC[:\s]+([A-Z]{3,4}\d{6}[A-Z0-9]{3})/i);
const serviceMatch = aiResponse.match(/servicio.*?:\s*(.+?)(?:\n|$)/i);

return {
  json: {
    log_data: {
      session_id: $('Data Processor').item.json.session_id,
      client_phone: $('Data Processor').item.json.phone_number,
      client_name: null,
      user_messages: [{
        message: $('Data Processor').item.json.message_text,
        timestamp: new Date($('Data Processor').item.json.start_time).toISOString()
      }],
      ai_responses: [{
        message: aiResponse,
        timestamp: new Date().toISOString()
      }],
      processing_time_ms: processingTime,
      client_rfc: rfcMatch ? rfcMatch[1] : null,
      detected_service_name: serviceMatch ? serviceMatch[1].trim() : null,
      escalated: aiResponse.toLowerCase().includes('escalar'),
      last_interaction_at: new Date().toISOString()
    },
    phone_number: $('Data Processor').item.json.phone_number,
    ai_output: aiResponse
  }
};
